import './Formulario.css'
import axios from 'axios'
import { useForm } from 'react-hook-form'
import { useState} from 'react';

const Formulario = () => {

    const {handleSubmit, register} = useForm();
    const [ ShortenedUrl, SetShortenedUrl] = useState('');
    const [ BaseUrl, SetBaseUrl ] = useState();

    const BaseUrlInput = document.querySelector('#BaseUrl');

    const Data = {
        BaseUrl: BaseUrl
    };

    function onChangeForm () {
        SetBaseUrl(BaseUrlInput.value)
    };
    function onSubmitForm () {
        if (BaseUrl) {
            axios.post('http://localhost:3001/api/v1/shortener', Data)
            .then((response) => {
                console.log(response.data.data.attributes)
                SetShortenedUrl(response.data.data.attributes.ShortenedUrl)
        })}
    }
    return <div className='formulario'>
        <form onSubmit={handleSubmit(onSubmitForm)}>
            <label htmlFor='BaseUrl'>Insira o link a ser encurtado:</label>
            <input  type='text' id='BaseUrl' onChange={onChangeForm}{...register('BaseUrl')}></input>
            <button type='submit'>Encurtar!</button>
            <p>{ShortenedUrl}</p>
        </form>
    </div>
}

export default Formulario
