import './Formulario.css'
import axios from 'axios'
import { useForm } from 'react-hook-form'
import { useState} from 'react';

const Formulario = () => {

    const {handleSubmit} = useForm();
    const [ ShortenedUrl, SetShortenedUrl] = useState();
    const [ BaseUrl, SetBaseUrl ] = useState('');

    const Data = {
        BaseUrl: BaseUrl
    };

    function Clear () {
        return [SetShortenedUrl(''), document.getElementById('BaseUrl').value = '']
    }

    function onChangeForm (event) {
        SetBaseUrl(event.target.value)
    };
    function onSubmitForm () {
                axios.post('http://localhost:3001/api/v1/shortener', Data)
            .then((response) => {
                console.log(response.data.ShortenedUrl)
                SetShortenedUrl(response.data.ShortenedUrl)
        })
    }
    return <div className='formulario'>
        <form onSubmit={handleSubmit(onSubmitForm)}>
            <label htmlFor='BaseUrl'>Insira o link a ser encurtado:</label>
            <input  type='text' id='BaseUrl' name='BaseUrl' value={BaseUrl} onChange={onChangeForm} required/>
            <button type='submit'>Encurtar!</button>
            <button onClick={Clear}>Limpar!</button>
            {ShortenedUrl &&
                <p>
                <span>Shortened URL: </span>
                {ShortenedUrl}
                </p>}
        </form>
    </div>
}

export default Formulario
