import './Formulario.css'
import axios from 'axios'
import { useForm } from 'react-hook-form'
import { useState} from 'react';
import { HiClipboardDocument } from 'react-icons/hi2'
import { motion } from 'framer-motion'


const Formulario = () => {

    const {register, handleSubmit, formState: { errors }} = useForm();
    const [ ShortenedUrl, SetShortenedUrl] = useState('');
    const [ BaseUrl, SetBaseUrl ] = useState('');

    const Data = {
        BaseUrl: BaseUrl
    };

    const Popup = document.querySelector('#popup_copiarbtn')

    const Link = document.querySelector('#link')

    const Copiaricon = document.querySelector('#copiaricon')
    
    const Paragrform = document.querySelector('#paragrform')


    function CopiarTexto () {
        navigator.clipboard.writeText(Link.innerHTML)
        Popup.style.display = 'block'
    }

    let urltext;
    if (ShortenedUrl) {
        urltext = <span id='urltext'>URL encurtado:</span>
        Copiaricon.style.display = 'block'
        Paragrform.style.backgroundColor = 'lightgreen'
    }
    else {
        urltext = null 
    }


    function Clear () {
        SetBaseUrl('')
        SetShortenedUrl('')
    }

    function onChangeForm (event) {
        SetBaseUrl(event.target.value)
    };
    function onSubmitForm () {
                axios.post('http://localhost:3001/api/v1/shortener', Data)
            .then((response) => {
                console.log(response.data.ShortenedUrl)
                SetShortenedUrl(response.data.ShortenedUrl)})

    }

    return <div className='formulario'>
        <form onSubmit={handleSubmit(onSubmitForm)}>
            <label htmlFor='BaseUrl'>Cole o URL a ser encurtado:</label>
            <input  type='text' id='BaseUrl' {...register('BaseUrl', { required: true})} value={BaseUrl} onChange={onChangeForm} placeholder='Insira a sua URL aqui'/>
            { errors.BaseUrl && <span className='linkerror'>Inserir um URL é obrigatório.</span>}
            <button type='submit' className='encurtarbtn'>Encurtar URL</button>
            <button onClick={Clear} className='limparbtn' type='button'>Limpar</button>
            { BaseUrl &&
            <motion.div className='divparagrform'>
                <p id='paragrform'>
                {urltext}
                <span id ='link' className='link'>{ShortenedUrl}</span>
                <button type='button' className='copiarbtn'><HiClipboardDocument onClick={CopiarTexto} id ='copiaricon' className='copiaricon'/></button>
                </p>
            </motion.div>}
        </form>
        { BaseUrl &&
            <span className='popup_copiarbtn' id='popup_copiarbtn'>Copiado <span className='popupshortenedurl'>{ShortenedUrl}</span> para a área de transferência</span>}

            
    </div>

    
}

export default Formulario
