import './Formulario.css'
import axios from 'axios'
import { useForm } from 'react-hook-form'
import { useState} from 'react';
import { useEffect } from 'react'
import { HiClipboardDocument } from 'react-icons/hi2'


const Formulario = () => {

    const {register, handleSubmit, formState: { errors }} = useForm();
    const [ ShortenedUrl, SetShortenedUrl] = useState('');
    const [ BaseUrl, SetBaseUrl ] = useState('');
    const [ Visibility, SetVisibility] = useState(false)

    const Data = {
        BaseUrl: BaseUrl
    };

    const Popup = document.querySelector('#popup_copiarbtn')

    const Link = document.querySelector('#link')

    function CopiarTexto () {
        navigator.clipboard.writeText(Link.innerHTML)
        Popup.style.display = 'block'
    }

    if (Visibility === true) {
        Link.style.display = 'block'
    } 


    function Clear () {
        SetBaseUrl('')
        SetShortenedUrl('')
    }

    function onChangeForm (event) {
        SetBaseUrl(event.target.value)
    };
    function onSubmitForm () {
                axios.post('http://localhost:3001/api/v1/shortener', Data)
            .then((response) => {
                console.log(response.data.ShortenedUrl)
                SetShortenedUrl(response.data.ShortenedUrl)})
                SetVisibility(true)

    }

    return <div className='formulario'>
        <form onSubmit={handleSubmit(onSubmitForm)}>
            <label htmlFor='BaseUrl'>Insira o URL a ser encurtado:</label>
            <input  type='text' id='BaseUrl' {...register('BaseUrl', { required: true})} value={BaseUrl} onChange={onChangeForm}/>
            { errors.BaseUrl && <span className='linkerror'>Inserir um URL é obrigatório.</span>}
            <button type='submit' className='encurtarbtn'>Encurtar!</button>
            <button onClick={Clear} className='limparbtn' type='button'>Limpar!</button>
            { BaseUrl &&
                <p>
                <span id='urltext'>URL encurtado:</span>
                <span id ='link' className='link'>{ShortenedUrl}</span>
                <button type='button' className='copiarbtn'><HiClipboardDocument onClick={CopiarTexto} className='copiaricon'/></button>
                </p>}
        </form>
        { BaseUrl &&
            <span className='popup_copiarbtn' id='popup_copiarbtn'>Copiado {ShortenedUrl} para a área de transferência</span>}

            
    </div>

    
}

export default Formulario
