import './Formulario.css'
import axios from 'axios'
import { useForm } from 'react-hook-form'
import { useState } from 'react';

const Formulario = () => {

    const {handleSubmit} = useForm();
    const [ ShortenedUrl, SetShortenedUrl] = useState();

    function onSubmitForm () {
        axios.post('http://localhost:3001/api/v1/shortener')
        .then((response) => {
            SetShortenedUrl(response.data.data.attributes.ShortenedUrl)
        })
    }

    function Clean () {
        const BaseUrlInput = document.querySelector('#BaseUrl');
        const LinkInput = document.querySelector('#LinkInput');
        BaseUrlInput.value = ''
        LinkInput.value = ''
    }
    return <div className='formulario'>
        <form onSubmit={handleSubmit(onSubmitForm)}>
            <label htmlFor='BaseUrl'>Insira o link a ser encurtado:</label>
            <input  type='text' id='BaseUrl' name='BaseUrl'></input>
            <button type='submit'>Encurtar!</button>
        </form>
    </div>
}

export default Formulario
